/****************************************************************** * Copyright (c) 2015 By Wlpava ******************************************************************/package com.wlpava.quye.controller;import java.util.Map;import javax.servlet.ServletRequest;import org.apache.shiro.authz.annotation.RequiresPermissions;import org.springframework.stereotype.Controller;import org.springframework.ui.Model;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import org.springframework.web.bind.annotation.RequestParam;import org.springframework.web.servlet.mvc.support.RedirectAttributes;import com.wlpava.core.web.Servlets;import com.wlpava.quye.QuyeController;import com.wlpava.quye.entity.Activity;import com.wlpava.system.SystemConstant;/** * XXX管理->XXX管理Contoller *  * @author wenlp */@Controller@RequestMapping("/dev/activity")public class ActivityController extends QuyeController {	@RequiresPermissions("activity:view")	@RequestMapping(value = { "list", "" }, method = RequestMethod.GET)	public String list(@RequestParam(value = "page", defaultValue = "1") Integer pageNumber, @RequestParam(value = "page.size", defaultValue = PAGE_SIZE) Integer pageSize, Model model,			ServletRequest request) {		Map<String, Object> searchParams = Servlets.getParametersStartingWith(request, REQUEST_PREFIX);		model.addAttribute("page", quyeServices.activityService.getPageable(searchParams, pageNumber, pageSize));		model.addAttribute("searchParams", Servlets.encodeParameterStringWithPrefix(searchParams, REQUEST_PREFIX));		return "quye/activity/list";	}	@RequiresPermissions("activity:create")	@RequestMapping(value = "/create", method = RequestMethod.GET)	public String showCreateForm(Model model) {		model.addAttribute("activity", new Activity());		model.addAttribute("op", SystemConstant.OperateEnum.create.getInfo());		return "quye/activity/edit";	}	@RequiresPermissions("activity:create")	@RequestMapping(value = "/create", method = RequestMethod.POST)	public String create(Activity activity, RedirectAttributes redirectAttributes) {		quyeServices.activityService.create(activity);		redirectAttributes.addFlashAttribute("msg", SystemConstant.OperateEnum.create.getInfo() + SystemConstant.OperateEnum.success.getInfo());		return "redirect:/activity";	}	@RequiresPermissions("activity:update")	@RequestMapping(value = "/{id}/update", method = RequestMethod.GET)	public String showUpdateForm(@PathVariable("id") Integer id, Model model) {		model.addAttribute("activity", quyeServices.activityService.findOne(id));		model.addAttribute("op", SystemConstant.OperateEnum.update.getInfo());		return "quye/activity/edit";	}	@RequiresPermissions("activity:update")	@RequestMapping(value = "/{id}/update", method = RequestMethod.POST)	public String update(Activity activity, RedirectAttributes redirectAttributes) {		quyeServices.activityService.update(activity);		redirectAttributes.addFlashAttribute("msg", SystemConstant.OperateEnum.update.getInfo() + SystemConstant.OperateEnum.success.getInfo());		return "redirect:/activity";	}	@RequiresPermissions("activity:delete")	@RequestMapping(value = "/{id}/delete", method = RequestMethod.GET)	public String showDeleteForm(@PathVariable("id") Integer id, Model model) {		model.addAttribute("activity", quyeServices.activityService.findOne(id));		model.addAttribute("op", SystemConstant.OperateEnum.delete.getInfo());		return "quye/activity/edit";	}	@RequiresPermissions("activity:delete")	@RequestMapping(value = "/{id}/delete", method = RequestMethod.POST)	public String delete(@PathVariable("id") Integer id, RedirectAttributes redirectAttributes) {		quyeServices.activityService.delete(id);		redirectAttributes.addFlashAttribute("msg", SystemConstant.OperateEnum.delete.getInfo() + SystemConstant.OperateEnum.success.getInfo());		return "redirect:/activity";	}}