/****************************************************************** * Copyright (c) 2015 By Wlpava ******************************************************************/package com.wlpava.quye.service;import java.util.List;import java.util.Map;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.data.domain.Page;import org.springframework.data.domain.PageRequest;import org.springframework.data.jpa.domain.Specification;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import com.wlpava.core.web.DynamicSpecifications;import com.wlpava.core.web.SearchFilter;import com.wlpava.quye.QuyeService;import com.wlpava.quye.entity.ApplyDetail;/** * XXX管理->XXX管理Service *  * @author wenlp */@Service@Transactional(readOnly = true)public class ApplyDetailService extends QuyeService {	private static Logger log = LoggerFactory.getLogger(ApplyDetailService.class);	public Page<ApplyDetail> getPageable(Map<String, Object> searchParams, Integer pageNumber, Integer pageSize) {		PageRequest pageRequest = buildPageRequest(pageNumber, pageSize);		Map<String, SearchFilter> filters = SearchFilter.parse(searchParams);		Specification<ApplyDetail> specification = DynamicSpecifications.bySearchFilter(filters.values(), ApplyDetail.class);		return quyeDaos.applyDetailDao.findAll(specification, pageRequest);	}	public ApplyDetail findOne(Integer applyDetailId) {		return quyeDaos.applyDetailDao.findOne(applyDetailId);	}	public List<ApplyDetail> findAll() {		return (List<ApplyDetail>) quyeDaos.applyDetailDao.findAll();	}		@Transactional(readOnly = false)	public ApplyDetail create(ApplyDetail applyDetail) {		return quyeDaos.applyDetailDao.save(applyDetail);	}	@Transactional(readOnly = false)	public ApplyDetail update(ApplyDetail applyDetail) {		return quyeDaos.applyDetailDao.save(applyDetail);	}	@Transactional(readOnly = false)	public void delete(Integer applyDetailId) {		quyeDaos.applyDetailDao.delete(applyDetailId);	}}