/****************************************************************** * Copyright (c) 2015 By Wlpava ******************************************************************/package com.wlpava.quye.service;import java.util.List;import java.util.Map;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.data.domain.Page;import org.springframework.data.domain.PageRequest;import org.springframework.data.jpa.domain.Specification;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import com.wlpava.core.web.DynamicSpecifications;import com.wlpava.core.web.SearchFilter;import com.wlpava.quye.QuyeService;import com.wlpava.quye.entity.Knowledge;/** * XXX管理->XXX管理Service *  * @author wenlp */@Service@Transactional(readOnly = true)public class KnowledgeService extends QuyeService {	private static Logger log = LoggerFactory.getLogger(KnowledgeService.class);	public Page<Knowledge> getPageable(Map<String, Object> searchParams, Integer pageNumber, Integer pageSize) {		PageRequest pageRequest = buildPageRequest(pageNumber, pageSize);		Map<String, SearchFilter> filters = SearchFilter.parse(searchParams);		Specification<Knowledge> specification = DynamicSpecifications.bySearchFilter(filters.values(), Knowledge.class);		return quyeDaos.knowledgeDao.findAll(specification, pageRequest);	}	public Knowledge findOne(Integer knowledgeId) {		return quyeDaos.knowledgeDao.findOne(knowledgeId);	}	public List<Knowledge> findAll() {		return (List<Knowledge>) quyeDaos.knowledgeDao.findAll();	}		@Transactional(readOnly = false)	public Knowledge create(Knowledge knowledge) {		return quyeDaos.knowledgeDao.save(knowledge);	}	@Transactional(readOnly = false)	public Knowledge update(Knowledge knowledge) {		return quyeDaos.knowledgeDao.save(knowledge);	}	@Transactional(readOnly = false)	public void delete(Integer knowledgeId) {		quyeDaos.knowledgeDao.delete(knowledgeId);	}}