/****************************************************************** * Copyright (c) 2015 By Wlpava ******************************************************************/package com.wlpava.quye.service;import java.util.List;import java.util.Map;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.data.domain.Page;import org.springframework.data.domain.PageRequest;import org.springframework.data.jpa.domain.Specification;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import com.wlpava.core.web.DynamicSpecifications;import com.wlpava.core.web.SearchFilter;import com.wlpava.quye.QuyeService;import com.wlpava.quye.entity.Routing;/** * XXX管理->XXX管理Service *  * @author wenlp */@Service@Transactional(readOnly = true)public class RoutingService extends QuyeService {	private static Logger log = LoggerFactory.getLogger(RoutingService.class);	public Page<Routing> getPageable(Map<String, Object> searchParams, Integer pageNumber, Integer pageSize) {		PageRequest pageRequest = buildPageRequest(pageNumber, pageSize);		Map<String, SearchFilter> filters = SearchFilter.parse(searchParams);		Specification<Routing> specification = DynamicSpecifications.bySearchFilter(filters.values(), Routing.class);		return quyeDaos.routingDao.findAll(specification, pageRequest);	}	public Routing findOne(Integer routingId) {		return quyeDaos.routingDao.findOne(routingId);	}	public List<Routing> findAll() {		return (List<Routing>) quyeDaos.routingDao.findAll();	}		@Transactional(readOnly = false)	public Routing create(Routing routing) {		return quyeDaos.routingDao.save(routing);	}	@Transactional(readOnly = false)	public Routing update(Routing routing) {		return quyeDaos.routingDao.save(routing);	}	@Transactional(readOnly = false)	public void delete(Integer routingId) {		quyeDaos.routingDao.delete(routingId);	}}