/****************************************************************** * Copyright (c) 2015 By Wlpava ******************************************************************/package com.wlpava.system.controller;import java.util.Date;import java.util.Map;import javax.servlet.ServletRequest;import org.apache.shiro.authz.annotation.RequiresPermissions;import org.springframework.stereotype.Controller;import org.springframework.ui.Model;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import org.springframework.web.bind.annotation.RequestParam;import org.springframework.web.servlet.mvc.support.RedirectAttributes;import com.wlpava.core.web.Servlets;import com.wlpava.system.BaseController;import com.wlpava.system.SystemConstant;import com.wlpava.system.entity.Bulletin;/** * 物业管理->公告新闻管理Contoller *  * @author wenlp */@Controller@RequestMapping("/dev/bulletin")public class BulletinController extends BaseController {	@RequiresPermissions("bulletin:view")	@RequestMapping(value = { "list", "" }, method = RequestMethod.GET)	public String list(@RequestParam(value = "page", defaultValue = "1") Integer pageNumber, @RequestParam(value = "page.size", defaultValue = PAGE_SIZE) Integer pageSize, Model model,			ServletRequest request) {		Map<String, Object> searchParams = Servlets.getParametersStartingWith(request, REQUEST_PREFIX);		model.addAttribute("page", systemServices.bulletinService.getPageable(searchParams, pageNumber, pageSize));		model.addAttribute("searchParams", Servlets.encodeParameterStringWithPrefix(searchParams, REQUEST_PREFIX));		return "system/bulletin/list";	}	@RequiresPermissions("bulletin:create")	@RequestMapping(value = "/create", method = RequestMethod.GET)	public String showCreateForm(Model model) {		model.addAttribute("bulletin", new Bulletin());		model.addAttribute("op", SystemConstant.OperateEnum.create.getInfo());		return "system/bulletin/edit";	}	@RequiresPermissions("bulletin:create")	@RequestMapping(value = "/create", method = RequestMethod.POST)	public String create(Bulletin bulletin, RedirectAttributes redirectAttributes) {		bulletin.setSource(currentUser().loginName);		bulletin.setCreateTime(new Date());		systemServices.bulletinService.create(bulletin);		redirectAttributes.addFlashAttribute("msg", SystemConstant.OperateEnum.create.getInfo() + SystemConstant.OperateEnum.success.getInfo());		return "redirect:/bulletin";	}	@RequiresPermissions("bulletin:update")	@RequestMapping(value = "/{id}/update", method = RequestMethod.GET)	public String showUpdateForm(@PathVariable("id") Integer id, Model model) {		model.addAttribute("bulletin", systemServices.bulletinService.findOne(id));		model.addAttribute("op", SystemConstant.OperateEnum.update.getInfo());		return "system/bulletin/edit";	}	@RequiresPermissions("bulletin:update")	@RequestMapping(value = "/{id}/update", method = RequestMethod.POST)	public String update(Bulletin bulletin, RedirectAttributes redirectAttributes) {		Bulletin oldBulletin = systemServices.bulletinService.findOne(bulletin.getId());		bulletin.setCreateTime(oldBulletin.getCreateTime());		systemServices.bulletinService.update(bulletin);		redirectAttributes.addFlashAttribute("msg", SystemConstant.OperateEnum.update.getInfo() + SystemConstant.OperateEnum.success.getInfo());		return "redirect:/bulletin";	}	@RequiresPermissions("bulletin:delete")	@RequestMapping(value = "/{id}/delete", method = RequestMethod.GET)	public String showDeleteForm(@PathVariable("id") Integer id, Model model) {		model.addAttribute("bulletin", systemServices.bulletinService.findOne(id));		model.addAttribute("op", SystemConstant.OperateEnum.delete.getInfo());		return "system/bulletin/edit";	}	@RequiresPermissions("bulletin:delete")	@RequestMapping(value = "/{id}/delete", method = RequestMethod.POST)	public String delete(@PathVariable("id") Integer id, RedirectAttributes redirectAttributes) {		systemServices.bulletinService.delete(id);		redirectAttributes.addFlashAttribute("msg", SystemConstant.OperateEnum.delete.getInfo() + SystemConstant.OperateEnum.success.getInfo());		return "redirect:/bulletin";	}	@RequiresPermissions("bulletin:view")	@RequestMapping(value = "/{id}/view", method = RequestMethod.GET)	public String showDetailForm(@PathVariable("id") Integer id, Model model) {		model.addAttribute("bulletin", systemServices.bulletinService.findOne(id));		return "system/bulletin/view";	}}